import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function QuizTakePage() {
    const [quizzes, setQuizzes] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [score, setScore] = useState(0);
    const [answeredQuestions, setAnsweredQuestions] = useState(0); // Track number of questions answered
    const [isLoading, setIsLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch quiz data from the server
        const fetchQuizzes = async () => {
            try {
                const response = await fetch('http://localhost:3001/get-quizzes');
                const data = await response.json();
                setQuizzes(data);
            } catch (error) {
                console.error('Error fetching quizzes:', error);
            } finally {
                setIsLoading(false);
            }
        };
        fetchQuizzes();
    }, []);

    const handleAnswer = (selectedOption) => {
        const currentQuestion = quizzes[0].questions[currentQuestionIndex];

        if (selectedOption === currentQuestion.correctAnswer) {
            setScore((prevScore) => prevScore + 1);
        }

        setAnsweredQuestions((prevAnswered) => prevAnswered + 1); // Increment answered questions

        if (currentQuestionIndex < quizzes[0]?.questions.length - 1) {
            setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
        } else {
            // Redirect to QuizResultsPage with score after the last question
            navigate('/score', { state: { score: score + (selectedOption === currentQuestion.correctAnswer ? 1 : 0), total: quizzes[0]?.questions.length, answered: answeredQuestions + 1 } });
        }
    };

    const showScore = () => {
        navigate('/score', {
            state: {
                score,
                total: quizzes[0]?.questions.length,
                answered: answeredQuestions
            }
        });
    };

    if (isLoading) {
        return <p>Loading quiz...</p>;
    }

    if (quizzes.length === 0) {
        return <p>No quizzes available.</p>;
    }

    const currentQuestion = quizzes[0].questions[currentQuestionIndex];

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <h2>{quizzes[0].topic}</h2>
            <div style={{ marginTop: '20px' }}>
                <h3>Question {currentQuestionIndex + 1}</h3>
                <p>{currentQuestion.question}</p>
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {currentQuestion.options.map((option, index) => (
                        <li key={index} style={{ margin: '10px 0' }}>
                            <button
                                style={{
                                    padding: '10px 20px',
                                    fontSize: '16px',
                                    cursor: 'pointer',
                                    backgroundColor: '#007bff',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '5px',
                                }}
                                onClick={() => handleAnswer(option)}
                            >
                                {option}
                            </button>
                        </li>
                    ))}
                </ul>
            </div>
            <button
                onClick={showScore}
                style={{
                    marginTop: '20px',
                    padding: '10px 20px',
                    fontSize: '16px',
                    cursor: 'pointer',
                    backgroundColor: '#28a745',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                }}
            >
                Show Score
            </button>
        </div>
    );
}

export default QuizTakePage;
