// src/pages/QuizTakePage.js
import React, { useState, useEffect } from 'react';

function QuizTakePage() {
    const [quizzes, setQuizzes] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Fetch quiz data from the server
        const fetchQuizzes = async () => {
            try {
                const response = await fetch('http://localhost:3001/get-quizzes');
                const data = await response.json();
                setQuizzes(data);
                setIsLoading(false);
            } catch (error) {
                console.error('Error fetching quizzes:', error);
                setIsLoading(false);
            }
        };
        fetchQuizzes();
    }, []);

    const handleNextQuestion = () => {
        if (currentQuestionIndex < quizzes[0]?.questions.length - 1) {
            setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
        } else {
            alert('Quiz completed!');
        }
    };

    if (isLoading) {
        return <p>Loading quiz...</p>;
    }

    if (quizzes.length === 0) {
        return <p>No quizzes available.</p>;
    }

    const currentQuestion = quizzes[0].questions[currentQuestionIndex];

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <h2>{quizzes[0].topic}</h2>
            <div style={{ marginTop: '20px' }}>
                <h3>Question {currentQuestionIndex + 1}</h3>
                <p>{currentQuestion.question}</p>
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {currentQuestion.options.map((option, index) => (
                        <li key={index} style={{ margin: '10px 0' }}>
                            <button
                                style={{
                                    padding: '10px 20px',
                                    fontSize: '16px',
                                    cursor: 'pointer',
                                    backgroundColor: '#007bff',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '5px',
                                }}
                                onClick={handleNextQuestion}
                            >
                                {option}
                            </button>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default QuizTakePage;
